INSERT INTO `champion` (`id`, `name`, `champ_key`, `title`, `description`) VALUES
	(1, 'Annie', 'Annie', 'Şeytan Çekici', 'Noxus\'ta ezelden beri Noxus Üst Komutası tarafından yapılan kötülükleri benimsemeyenler vardı. Üst Komuta, kendini hükümdar ilan eden Prens Rachallion\'ın darbe girişimini henüz bastırmıştı ve yeni hükümete muhalefet eden herkesin tepesine binmek...'),
	(2, 'Olaf', 'Olaf', 'Başıbozuk', 'Çoğu kişi ölümden korkar. Olaf onlardan biri değil. Başıbozuk Olaf, sadece savaş çığlıklarının uğultusu ve çeliğin çeliğe çarpma sesi için yaşar. Zafere olan açlığı ve kolay unutulacak bir ölümle ölmenin korkusuyla, her çatışmaya gözü kapalı dalar...'),
	(3, 'Galio', 'Galio', 'Heybetli Muhafız', 'Taştan dev Galio, ışıltılı Demacia şehrinin kapılarında hiç yorulmaksızın nöbet tutar. Düşman büyücüleri durdurması için inşa edilmiş Galio\'nun bazen yıllarca hareketsiz şekilde beklediği olur; ta ki güçlü büyüler onu uyandırana dek. Harekete geçtiği...'),
	(4, 'Twisted Fate', 'TwistedFate', 'İskambil Ustası', 'Twisted Fate; hem kumarda hem aşkta yakaladığı zaferlerle bilinen, dünyanın büyük bir kısmını gezip dolaşmış, zenginlerle ahmakların hem hayranlığını hem düşmanlığını kazanmış, iyisiyle kötüsüyle yaygın bir şöhret edinmiş bir kumarbaz ve dolandırıcıdır...'),
	(5, 'Xin Zhao', 'XinZhao', 'Demacia\'nın Hizmetkârı', '\'\'Ölüm kaçınılmazdır; kişi sadece yenilgiden kaçınabilir.\'\'<br><br>Demacia Kralı Jarvan III ne zaman Kraliyet Sarayı\'nın tepesindeki parlak mermer balkondan halka seslenecek olsa, Xin Zhao yanındadır. Demacia\'nın Hizmetkârı adı takılan Xin Zhao, aslında...'),
	(6, 'Urgot', 'Urgot', 'Kumandanın Gururu', 'Bazı savaşçılar güçlü, kurnaz ya da silah kullanmakta yetenekli oldukları için yücelir. Bazıları da ölmeyi düpedüz reddederek. Bir zamanlar Noxus\'un ünlü askerlerinden olan Urgot, ikinci türe başlı başına bir örnek. Savaşta düşman saflarına pervasızca...'),
	(7, 'LeBlanc', 'Leblanc', 'Hilekâr', 'Her şehrin bir karanlık yanı vardır; zaten karanlık bir ün salmış olanların bile. Noxus - adı daima saygı ve tiksinti karışımı bir duyguyla anılmasına rağmen -  bu basit gerçeğin dışında kalmıyor. Karanlık ve dolambaçlı sokaklarının altında yer alan...'),
	(8, 'Vladimir', 'Vladimir', 'Kızıl Ölüm', 'Noxus\'la Fırtına Düzlükleri arasındaki dağlarda gizlenmiş bir tapınak vardır. Kadim ve tüyler ürpertici bir büyü geleneğinin sırları burada saklanır. Tapınağın çevresi, yanlışlıkla çok yaklaşanların kanı çekilmiş cesetleriyle doludur. Bunlar...'),
	(9, 'Fiddlesticks', 'Fiddlesticks', 'Kıyamet Habercisi', 'Fiddlesticks neredeyse 20 yıldır, Savaş Enstitüsü\'nün en doğusundaki ayin odasında yalnız başına durmaktaydı. Tozla kaplı evinin küf kokulu karanlığını, sadece zümrüt rengi alevlerle yanan doğa üstü bakışları delip geçiyordu. Kıyametin Habercisi sessiz...'),
	(10, 'Kayle', 'Kayle', 'Adaletin Kılıcı', 'Ne zaman başladığı unutulmuş bir savaşın hâlâ devam ettiği uzak bir dünyada, Kayle büyük bir kahramandı. Kötülüğü, bulunduğu her yerde yok etmeye adanmış ölümsüz bir ırkın en güçlüsüydü. Kayle 10 bin yıl boyunca, zamanın kendisinden bile eskilerde...'),
	(11, 'Master Yi', 'MasterYi', 'Wuju Üstadı', 'Master Yi, kadim dövüş sanatı Wuju sayesinde, eylemleri düşünceleriyle bir olana dek vücudunu eğitip zihnini bilemiştir. Şiddete hep en son çare olarak başvurur ama kılıcını öylesine bir zarafet ve hızla kullanır ki, çözüm hep hızlı ve acısız olur...'),
	(12, 'Alistar', 'Alistar', 'Minotor', 'Büyük Duvar\'ın Minotor kabilelerinden çıkmış en güçlü savaşçılardan biri olan Alistar, kabilesini Valoran\'ın pek çok tehlikesinden korudu; ta ki Noxus ordusu gelene kadar. Alistar, General Boram Darkwill\'in oğlu ve Noxus keşif ordularının komutanı...'),
	(13, 'Ryze', 'Ryze', 'Rün Büyücüsü', '\'\'Bu dünyanın kıymetini bil. Dikilen her şey elbet yıkılır da.\'\'<br><br>Nicelerinin Runeterra\'daki en mahir büyücülerden biri saydığı Ryze, taşıması sıradan insanlar için imkansız bir yükü omuzlamış, kadim ve inatçı bir rün büyücüsü. Sınır tanımaz...'),
	(14, 'Sion', 'Sion', 'Ölü Dev', 'KAN.<br><br>KOKUSU GELİYOR.<br><br>İSTİYORUM. ACIYOR. LAZIM!<br><br>YAKLAŞTI. GELİYORLAR.<br><br>ZİNCİRLERİM YOK? ÖZGÜRÜM! ÖLDÜRMELİ!<br><br>YAKLAŞIN. EVET! ÖL! ÖL!<br><br>Bitti. Çok çabuk. Direnmediler. Daha fazla. Daha... fazlasını istiyorum...'),
	(15, 'Sivir', 'Sivir', 'Savaş Hanımı', '\'\'Paranın nereden geldiği değil, ne kadar ettiği önemlidir.\'\'<br><br>Sivir, zanaatını Shurima çöllerinde icra eden bir avcı ve paralı asker komutanı. Mücevherlerle kaplı, efsanevi dörtlü bıçağıyla giriştiği nice savaşta, dudak uçuklatan ücretini...'),
	(16, 'Soraka', 'Soraka', 'Yıldız Çocuk', 'Yıldızların hediyesiyle kutsanmış bir şifacı olan Soraka, tüm canlıları yürekten sever. Bir zamanlar ilahi bir varlıktı, ama ölümsüzlüğünü feda ederek ölümlüler dünyasına girdi. Kötülük Valoran\'daki hayatı tehdit ettikçe, Soraka kendine huzur vermeyecek...'),
	(17, 'Teemo', 'Teemo', 'Çevik İzci', 'Teemo, Bandle Şehri\'ndeki yordle kardeşleri arasında bir efsanedir. Yordlelara göre, Teemo\'da bir tuhaflık var ama ne olduğunu tam bilemiyorlar. Teemo diğer yordlelarla vakit geçirmeyi sevse de, Bandle Şehri\'nin devam eden savunmasına katılmak için sık...'),
	(18, 'Tristana', 'Tristana', 'Yaman Topçu', 'Boyu ile elindeki portatif topu neredeyse aynı olan bu yordle askeri tarafından ispatlanmış olduğu üzere, büyüklük boyla ölçülmez. Tristana binbir hengâmenin koptuğu bu dünyada hiçbir zorluk karşısında geri adım atmaz. Savaşçı becerileri, sarsılmaz...'),
	(19, 'Warwick', 'Warwick', 'Zaun\'un Kafes Kaçkını Belası', 'Warwick, Zaun\'un gri sokaklarında avlanan bir canavar. Muazzam derecede acı veren deneyler sonucu değişim geçiren vücudu, damarlarını simyasal bir öfke ile dolduran karmaşık bir hazne ve pompa sistemi ile donatılmış. Gölgelerin arasından bir anda...'),
	(20, 'Nunu', 'Nunu', 'Yeti Süvarisi', 'Bazen arkadaşlık bağları, kan bağlarından bile daha güçlü olur. Bu bağlar bir de korkusuz bir oğlanı korkunç bir Yeti\'ye bağlıyorsa, çekinilecek bir şey haline gelir. Korkunç bir canavarı ehlileştirme görevi verilen Nunu, başkalarının zincirle...'),
	(21, 'Miss Fortune', 'MissFortune', 'Ödül Avcısı', '\'\'Risk ne kadar büyükse ödül de o kadar dolgun olur.\'\'<br><br>Güzellikte de, tehditkârlıkta da Miss Fortune\'un yanına yaklaşabilecek çok az kişi vardır. Bilgewater\'ın en ünlü ödül avcılarından olan Miss Fortune\'un efsanesi, mermilerle delik deşik olmuş...'),
	(22, 'Ashe', 'Ashe', 'Ayaz Okçu', 'Ashe, çok eski çağlarda büyülü buzdan yapılmış yayından attığı her okla, usta bir okçu olduğunu kanıtlar. Her hedefi dikkatle seçip doğru anı bekler, sonra isabetli ve kuvveti bir saldırı yapar. Bu açık görüşten ve odaklanma yeteneğinden, ona Freljord\'u...'),
	(23, 'Tryndamere', 'Tryndamere', 'Barbar Kral', 'Sonsuz hiddetinden ve öfkesinden güç alan Tryndamere; tundrayı yarıp geçiyor ve savaş sanatında ustalaşmak için Freljord\'un en iyi savaşçılarına meydan okuyor. İçi gazapla dolan savaşçı, kabilesini kırıp geçiren kişiden intikam almak istiyor ve...'),
	(24, 'Jax', 'Jax', 'Silah Ustası', 'Bir şampiyonun League of Legends\'a (Efsaneler Ligi) katılmadan önceki hareketlerinden çok, sonraki hareketleriyle tanımlanması, oldukça nadir görülen bir durumdur. Şu an Savaş Enstitüsü\'nde bulunan en verimli turnuva savaşçısı olması muhtemel Jax\'ın...'),
	(25, 'Morgana', 'Morgana', 'Düşmüş Melek', 'Çok uzaklarda; ölümsüzlükle kutsanmış, zarif ve güzel kanatlı varlıkların yaşadığı, başlangıcı unutulmuş bir çatışmanın halen devam ettiği bir dünya var. Bir çok çatışma gibi bu savaş da aileleri parçaladı. Taraflardan biri kendisini, dünyayı kendi...'),
	(26, 'Zilean', 'Zilean', 'Zaman Bekçisi', 'Bir zamanlar, çorak Urtistan topraklarında ihtişamlı bir şehir vardı. Büyük Duvar\'ın aşağısındaki çoğu ülke gibi, o da uzun zaman önce korkunç bir Rün Savaşı sırasında yıkılmıştı. Fakat bu şehirden bir kişi sağ çıktı: Zilean adlı bir büyücü. Zamanı...'),
	(27, 'Singed', 'Singed', 'Deli Kimyager', 'Singed, Zaun\'un saygıdeğer kimyagerlerinin soyundan gelir. Küçüklüğünde bile, karıştırdığı iksirler yaşıtlarınınkileri açık ara geçerdi. Daha sıradan kimyager meslektaşları arasından hızla sıyrıldı. Noxus ordusunun Ionia seferinde kazançlı bir ücretle...'),
	(28, 'Evelynn', 'Evelynn', 'Dul Bırakan', 'Eli çabuk ve her saldırısı ölümcül olan Evelynn, Runeterra\'nın en ölümcül (ve pahalı!) suikastçılarından biridir. İstediği zaman gölgelere karışabilme yeteneği sayesinde, avlarını sabırla takip edip saldırmak için doğru anı bekler. Evelynn\'in pek de...'),
	(29, 'Twitch', 'Twitch', 'Veba Getiren', 'B.Ö.C.E.K. Olay Tutanağı<br>İsnat Olunan Fiil: İş Cinayeti<br>Vaka Durumu: Faili Meçhul<br>Soruşturmayı Yürüten: Rol, P.<br><br>Ekibimiz şüpheli şahıs ve suç işlendiği ihbarını değerlendirerek, olaya müdahale etmek üzere Kanalizasyon İşletmesi, Sektör...'),
	(30, 'Karthus', 'Karthus', 'Ölümşakıyan', '\'\'Ölüm yolun sonu değil, daha başı...\'\'<br><br>Dehşet veren repertuvarı ve kâbuslardan fırlamış görünümüyle ecelin haberciliğini yapan Karthus, ölmeyen bir ruh. Hayattakiler ebedi hortlaklığın düşüncesiyle bile korkuya kapılırken, Karthus onda sadece...'),
	(31, 'Cho\'Gath', 'Chogath', 'Hiçlik\'in Dehşeti', 'Dünyalar arasında, boyutlar arasında bir yer var. Bazıları burayı Dışarısı olarak bilir, bazılarıysa Bilinmezlik olarak. Gerçekten bilenler ise buraya Hiçlik der. Hiçlik, adının aksine hiçbir şeyin olmadığı bir yer değil; ağza alınamayacak şeylerin...'),
	(32, 'Amumu', 'Amumu', 'Hüzünlü Mumya', '\'\'Ölülerden bile daha yalnız olmak mümkün.\'\'<br><br>Shurima\'lı yalnız ve melankolik Amumu, bir arkadaş bulabilmek ümidiyle dünyayı karış karış dolaşıyor. Etkisi altında olduğu kadim lanet yüzünden dokunduğu, sevdiği her şeyin felaketi olduğundan...'),
	(33, 'Rammus', 'Rammus', 'Zırhlı Armadillo', '\'\'Tamam.\'\'<br><br>Nicelerinin yerlere göklere sığdıramadığı, kimilerinin görmezden geldiği ama herkesin akıl erdirmeye çalıştığı tuhaf yaratık Rammus, tam anlamıyla bir muamma. Dikenli kabuğunun korumasında adım attığı her yerde, kökenine dair...'),
	(34, 'Anivia', 'Anivia', 'Buz Ankası', 'Freljord\'un en eski koruyucularından biri olan Anivia, en soğuk kış ikliminin ve buz büyüsünün vücut bulmuş hali. Diyarın tüm gücüne ve gazabına hükmeden bu varlık, kar fırtınası ve sert rüzgârı kontrol ederek memleketini savunuyor. Bu iyiliksever ama...'),
	(35, 'Shaco', 'Shaco', 'İblis Soytarı', 'Çoğu kişi ölümü herhalde komik bulmuyordur. Zaten değildir de, tabii Shaco değilsen. Shaco\'ysan, feci komiktir. Shaco, Valoran\'ın ilk tam teşekküllü katil komedyeni. Karşısındakini öldürene kadar şaka yapar, sonra da kendisi güler. İblis Soytarı olarak...'),
	(36, 'Dr. Mundo', 'DrMundo', 'Zaun\'un Delisi', '\'\'Zaun\'un delisinden sakının. Onun gözünde zaten ölüsünüz.\'\'<br><br>Artık Dr. Mundo olarak tanınan adamın, vicdan namına hiçbir şeyi olmadan doğduğu söylenir. Bunun yerine, deneylerle acı vermeye yönelik dindirilemez bir arzuya sahipti. Beş yaşına...'),
	(37, 'Sona', 'Sona', 'Müzik Dehası', 'Sona gerçek ailesini hiç hatırlamıyor. Daha bebekken, nerede yapıldığı bilinmeyen şahane bir kutunun içinde duran, unutulmuş çağlardan kalma bir enstrümanın üstünde, bir Ionia yetimhanesinin eşiğine terk edilmiş. Sona aşırı derecede uslu bir çocuktu...'),
	(38, 'Kassadin', 'Kassadin', 'Hiçlik Gezgini', 'Dünyalar arasında, boyutlar arasında bir yer vardır. Bazıları Dışarısı olarak bilir, bazılarıysa Bilinmezlik olarak. Gerçekten bilenler ise buraya Hiçlik derler. Hiçlik, adının aksine bomboş bir yer değil; ağza alınamayacak şeylerin, insan aklına...'),
	(39, 'Irelia', 'Irelia', 'Bıçakların İradesi', '\'\'Kılıç, kanla resim yapan bir ressamın fırçası gibi dalgalanıyor.\'\'<br><br>Runeterra üzerindeki en nefes kesici ve ölümcül dövüş sanatlarının birçoğunda Ionia imzası vardır. Bu sanatlar, Ionia\'lıların aydınlanma peşinde koştuklarının göstergelerinden...'),
	(40, 'Janna', 'Janna', 'Fırtınanın Hiddeti', 'Büyüyü öğrenilmiş yollarla yapmaktan vazgeçip, kendilerini doğanın temel güçlerine veren büyücüler vardır. Janna da bunlardan biri. Sihri ilk olarak, Zaun şehir devletinin kargaşasında büyüyen bir yetimken öğrendi. Janna sokaklarda yaşıyor, kıt kanaat...'),
	(41, 'Gangplank', 'Gangplank', 'Okyanus Şeytanı', '\'\'Sen daha altına pislerken, ben gırtlak kesip Noxus\'un savaş kalyonlarını batırıyordum toy oğlan. Aklını başına al!\'\'<br><br>Ne kadar acımasızsa sağı solu da bir o kadar belirsiz olan, yağmacı-talancıların tahtından indirilmiş kralı Gangplank, yedi...'),
	(42, 'Corki', 'Corki', 'Gözüpek Bombacı', 'Heimerdinger ve yordle yoldaşları Piltover\'a göç ettiklerinde, bilimi bir yaşam biçimi olarak benimsediler ve anında, tekmaturji çevrelerinde çığır açan bazı yenilikler yaptılar. Yordle\'lar boylarının kısalığını çalışkanlıklarıyla telafi eder. Gözüpek...'),
	(43, 'Karma', 'Karma', 'Bilgelik Ateşi', 'Karma, boyun eğmez bir iradeye ve sonsuz ruhani güce sahip bir kadın. Ionia ruhunun beden bulmuş hali. Savaş meydanında dostlarını kollayıp düşmanlarını geri püskürtüşü, müttefiklerine ilham veriyor. Kuvvetli bir lider olmasına karşın gelenekle devrim...'),
	(44, 'Taric', 'Taric', 'Valoran\'ın Koruyucusu', '\'\'En iyi silahlar hep güzeldir.\'\'<br><br>Muhafızın Sureti Taric, Runeterra\'nın hayat, sevgi ve güzellik bekçisi olarak akıl almaz bir güce sahip. Vazifesini ihmal ettiği için şerefi lekelenen ve bu yüzden memleketi Demacia\'dan sürgün edilen Taric...'),
	(45, 'Veigar', 'Veigar', 'Kötülüğün Minik Efendisi', 'Yordle deyince çoğu kişinin zihninde korkulacak bir şey canlanmaz. Bu herkesle iyi geçinen, yerden bitme ırk gayet yırtıcı olmasına karşın, yordle\'lara genelde bir miktar neşeyle yaklaşılır. Tiz sesleri, kendiliğinden sevimli görünüşleri daha iri...'),
	(48, 'Trundle', 'Trundle', 'Trol Kralı', 'Trundle, devasa, kurnaz ve sinsi bir trol. Döve döve her şeye istediğini yaptırabilir; buza bile. Kocaman, donmuş sopası ile rakiplerinin iliklerini dondurup, sivri buz parçalarıyla delik deşik eder. Bölgesine korkutucu derecede düşkün olan Trundle...'),
	(50, 'Swain', 'Swain', 'Taktik Üstadı', 'Swain\'in varlığının bilinen ilk kanıtı, Noxus\'lu bir revir doktorunun notlarıdır. Notlara göre Swain topallaya topallaya, bağırmadan, şikâyet etmeden koğuşa girmiş. Bacağı ortadan ikiye kırılmış haldeymiş. Kemiğin uçları derisinden çıkıyormuş. Küçük...'),
	(51, 'Caitlyn', 'Caitlyn', 'Piltover Şerifi', '\'\'Durma, kaç. Sana birkaç dakikalık avans vereceğim.\'\'<br><br>Piltover\'ın İlerleme Şehri olarak anılmasının bir nedeni de, suç oranının inanılmaz derecede düşük olmasıdır. Gerçi durum her zaman böyle değildi; özellikle tekmaturji araştırmaları için...'),
	(53, 'Blitzcrank', 'Blitzcrank', 'Büyük Buharlı Golem', 'Zaun, hem sihrin hem de bilimin ters gittiği bir yerdir. Deneylerin kontrolsüz doğası, şehri epey hırpalamıştır. Ancak Zaun\'un hoşgörülü kısıtlamaları, şehrin araştırmacılarına ve mucitlerine bilimin sınırlarını ister iyi ister kötü sonuçlarla daha...'),
	(54, 'Malphite', 'Malphite', 'Yektaş\'tan Kopan Parça', 'Her şeyin bütünün parçası olduğu, kusursuz ahenkli bir dünya vardır. Yektaş adlı bu dünya tüm varoluşun özüdür ve orada yaşayanlar da onun birer parçasıdır. Simetrisiyle ve belirsizliğin neredeyse olmayışıyla çok güzel bir yerdir. Orada yaşayan taşsı...'),
	(55, 'Katarina', 'Katarina', 'Sinsi Bıçak', 'Şiddetli bir öldürme içgüdüsü ile hareket eden Katarina, bir suikastçı olarak yeteneklerini Noxus\'un şanı ve ailesinin başarısı için kullanıyor. Azmi ve coşkusu onu çok daha büyük işler başarmaya itse de, bazen yolundan sapmasına neden olabiliyor...'),
	(56, 'Nocturne', 'Nocturne', 'Ebedi Kâbus', 'Nocturne gelmeden önce, insanlar rüyaların hayal güçlerinin ürünü, uyurken beyinlerinde çakan anlamsız görüntüler olduğuna inanırlardı. Uyku esnasında yaşanan bir dizi olay Lig sihirdarlarını etkilemeye başlayınca, bu inanç sarsıldı. Bazıları uykudan...'),
	(57, 'Maokai', 'Maokai', 'Çarpık Treant', '\'\'Her yanımda boş bedenler, ruhsuz ve korkusuz... Onlara korkuyu tattıracağım.\'\'<br><br>Maokai, Gölge Adalar\'ın korkunç doğadışı yaratıklarına karşı savaşan öfke dolu, dev bir treanttır. Büyülü bir felaketin evini yerle bir etmesiyle çarpık bir intikam...'),
	(58, 'Renekton', 'Renekton', 'Kumların Kasabı', '\'\'Kan ve intikam.\'\'<br><br>Renekton, Shurima\'nın kavrulmuş çöllerinden gelen Yükseliş\'e ermiş korkunç ve hiddetli bir varlıktır. Bir zamanlar Shurima ordularını sayısız zafere taşımış, imparatorluğun el üstünde tutulan savaşçısıydı. İmparatorluğun...'),
	(59, 'Jarvan IV', 'JarvanIV', 'Demacia\'nın Simgesi', '\'\'Yalnızca bir gerçek var ve onu kargımın ucunda bulacaksınız.\'\'<br><br>Yüzyıllardır Demacia\'nın kraliyet ailesi olan Lightshield soyundan gelenler, hayatlarını Demacia ilkelerine karşı gelenlere savaş açarak geçirmiştir. Lightshield ailesinin her...'),
	(60, 'Elise', 'Elise', 'Örümcek Kraliçe', '\'\'Güzellik de kudrettir ve bir kılıçtan daha keskin olabilir.\'\'<br><br>Her yanı sürgülenmiş, ışıksız Noxus sarayı Ölümsüz Hisar\'ın derinliklerinde yaşayan Elise, acımasız ve yırtıcı bir canavar. Vaktiyle güçlü bir ailenin hanımı olan Elise bir örümcek...'),
	(61, 'Orianna', 'Orianna', 'Mekanik Kız', 'Bir zamanlar Piltover\'da Corin Reveck diye bir adam yaşardı. Reveck, kızı Orianna\'yı her şeyden çok severdi. Orianna, dans konusunda çok yetenekli olmasına rağmen League of Legends\'ın (Efsaneler Ligi) şampiyonlarına derin bir hayranlık besliyordu. Bu...'),
	(62, 'Wukong', 'MonkeyKing', 'Maymun Kral', 'Rün Savaşları\'nın karmaşasında, devasa boyutlardaki bir rüntaşı Veba Ormanları\'nın derinliklerinde kayboldu. Orada, el değmemiş bir halde yüzyıllarca durdu ve çevresinde yaydığı kuvvetli büyüyle yaban hayatına bilinç ve yaşama gücü verdi. Bu güçten...'),
	(63, 'Brand', 'Brand', 'İntikam Ateşi', 'Lokfar diye bilinen uzak bir diyarda, Kegan Rodhe adında denizci bir haydut yaşardı. Meslektaşları gibi, Kegan da dostlarıyla birlikte engin denize açılır ve yollarına çıkacak kadar talihsiz olanların malını mülkünü çalardı. Kimilerine göre bir...'),
	(64, 'Lee Sin', 'LeeSin', 'Kör Keşiş', 'Lee Sin, gençliğin verdiği heyecanla sihirdar olmayı kafasına koymuştu. Akranlarında benzeri görülmemiş bir iradeye ve azme sahip olan delikanlı, becerileri ile o zamanki Yüksek Konsey Üyesi Reginald Ashram\'ın dikkatini çekti. Sihir Akademisi\'nde eğitim...'),
	(67, 'Vayne', 'Vayne', 'Gece Avcısı', 'Dünya her zaman insanların sandığı kadar uygar değildir. Büyünün en kara yollarını takip edip, Runeterra\'nın damarlarında akan karanlık güçlerle zehirlenenler de var. Shauna Vayne bu gerçeği çok iyi bilir.<br><br>Demacia\'nın seçkin ailelerinden birinin...'),
	(68, 'Rumble', 'Rumble', 'Mekanize Bela', '\'\'Iyy, yüzünü zırhımdan kazımak çok zamanımı alacak!\'\'<br><br>Rumble, yordlelar arasında bile en çelimsiz olanıydı. Durum böyle olunca kendisine sataşılmasına alışmıştı. Hayatta kalabilmek için yaşıtlarından daha kavgacı ve daha becerikli olmaktan başka...'),
	(69, 'Cassiopeia', 'Cassiopeia', 'Yılanın Şefkati', 'Cassiopeia; yarı insan yarı yılan biçiminde, tek bir bakışıyla bile ölüm saçabilen, dehşet verici bir yaratık. Noxus\'un en nüfuzlu ailelerinden birinin en küçük kızı olan Cassiopeia, bir zamanlar taş kalpleri bile tatlı dili ve güzelliği ile...'),
	(72, 'Skarner', 'Skarner', 'Kristal Öncü', '\'\'Biz bütünüz. Parçalanmayız.\'\'<br><br>Skarner, Shurima\'daki gizli bir vadiden gelmiş, devasa bir kristal akrep. Ruhları atalarının hâlâ yaşayan düşünce ve anılarını barındıran, güçlü yaşam kristalleriyle bütünleştiği için, kadim Tözkabuk soyundan gelen...'),
	(74, 'Heimerdinger', 'Heimerdinger', 'Saygın Mucit', 'Profesör Cecil B. Heimerdinger\'in günlüğünden:<br><br>10.14<br><br>09.15<br><br> Bandle Şehri\'nde optimal hava koşulları hâkim. Atmosferdeki basınç, bugünkü deney için ideal!<br><br> Tridiminyumobülatör\'ün beşinci denemesini bu akşamüstü...'),
	(75, 'Nasus', 'Nasus', 'Kumların Efendisi', '\'\'Çöken her şey tekrar yükselecek.\'\'<br><br>Nasus, antik Shurima\'dan gelen çakal başlı Yükselmiş bir varlık. Çöl halkları tarafından bir yarı tanrı olarak kabul edilen bir kahraman. Keskin bir zekâya sahip olan bu kahraman, bilginin muhafızı olmuştu ve...'),
	(76, 'Nidalee', 'Nidalee', 'Yabani Avcı', 'Büyük Duvar\'ın güneyindeki harap ve tehlikeli topraklarda değil şampiyonlar, doğru düzgün insan bile bulunmaz. O dünyanın büyük kısmı, özellikle de gizemli Kumungu Ormanı, geçmişte yaşanan Rün Savaşları\'nın derin izlerini hâlâ taşır. Bu tuhaf yerlerde...'),
	(77, 'Udyr', 'Udyr', 'Ruh Gezgini', 'Udyr sıradan bir insandan çok daha fazlasıdır; dört ilksel hayvan ruhunun bedeninde buluştuğu kişidir o. Udyr bu ruhların hayvansı yönleriyle bir olunca, özel güçlerinden yararlanabilir: Kaplan ona hız ve atılganlık, kaplumbağa dayanıklılık, ayı güç...'),
	(78, 'Poppy', 'Poppy', 'Çekicin Muhafızı', '\'\'Ben kahraman değilim. Çekiç taşıyan bir yordle\'ım, hepsi bu.\'\'<br><br>Runeterra\'da gözüpek şampiyon kıtlığı olduğunu kimse söyleyemese de, bu şampiyonların pek azı Poppy kadar azimlidir. Boyunun iki katı bir çekiç taşıyan bu kararlı yordle; yıllar...'),
	(79, 'Gragas', 'Gragas', 'Kavgacı Ayyaş', 'Gragas\'ın dövüşmekten daha çok önemsediği bir şey varsa, o da içmektir. Daha da, daha da sert bira içme isteğiyle, sürekli mayalama kazanına atacak güçlü, olmadık malzemeler arar. Aniden parlayan, sağı solu belli olmayan bu yıllanmış alemci, kafa...'),
	(80, 'Pantheon', 'Pantheon', 'Savaş Üstadı', '\'\'Karşıma gerçek bir şampiyon çıkarın hatta yüz tanesi gelsin. İşte o zaman, sonsuza dek anlatılacak bir savaş görürsünüz.\'\'<br><br>Emsali olmayan savaşçı Pantheon, savaşın amansız bir timsalidir. Bu şampiyon, cengâver Rakkor halkının yaşadığı Targon...'),
	(81, 'Ezreal', 'Ezreal', 'Dahi Kâşif', 'Runeterra\'nın en ücra ve terk edilmiş yörelerini dahi gezmiş olan bu genç ve atılgan maceracı, Antik Shurima\'nın kumlar altına gömülü kalıntılarına yaptığı bir inceleme gezisi esnasında inanılmaz büyü gücüne sahip bir muska keşfetti. Büyük ihtimalle...'),
	(82, 'Mordekaiser', 'Mordekaiser', 'Demir Hortlak', '\'\'Her şey ölümü tatmalı... Ama ben yaşamaya devam ediyorum.\'\'<br><br>Gölge Adalar\'da kol gezen ruhların en korkunç ve en nefret dolu olanı, uğursuz hortlak Mordekaiser\'dir. Yüzyıllardır kara büyü ve kendi karanlık iradesinin gücüyle gerçek ölümden...'),
	(83, 'Yorick', 'Yorick', 'Ruhların Çobanı', '\'\'Adaların feryatları kulağımda çınlıyor.\'\'<br>Uzun zaman önce unutulmuş bir inanç nizamının hayatta kalan son üyesi Yorick, ölülerin gücüyle hem kutsanmış hem de lanetlenmiş biri. Gölge Adalar\'a sıkışıp kalan Yorick\'in çürüyen cesetler ve feryatlar...'),
	(84, 'Akali', 'Akali', 'Gölgenin Yumruğu', 'Ionia Adaları\'nda kendini dengeyi korumaya adamış kadim bir tarikat var. Düzen, karmaşa, ışık, karanlık; kâinatın yaradılışı gereği, her şey mükemmel bir uyum içinde olmalı. Kinkou adıyla bilinen bu tarikat, dünya üzerinde amaçlarını gerçekleştirmeleri...'),
	(85, 'Kennen', 'Kennen', 'Fırtınanın Kalbi', 'Kökeni Ionia Adaları\'nda olan, dengeyi korumaya adanmış kadim bir tarikat vardır. Düzen, karmaşa, ışık, karanlık -- hepsi kusursuz bir ahenk içinde olmalıdır; çünkü evrenin gidişatı böyledir. Bu tarikat Kinkou olarak bilinmekte ve dünyadaki amaçlarını...'),
	(86, 'Garen', 'Garen', 'Demacia\'nın Kudreti', 'Valoran\'ın her yerinde Demacia ordusunun azmi ya övülür ya da nefret uyandırır; ama daima saygı görür. Demacia\'nın \'\'sıfır hoşgörü\'\' şeklinde ifade edilen ahlaki düsturu hem siviller hem de askerler tarafından sıkı sıkıya uygulanır. Bu, çatışma...'),
	(89, 'Leona', 'Leona', 'Seher Işığı', '\'\'Güneş gibi ışıldıyorsan Güneş gibi yakmayı da bileceksin.\'\'<br><br>Güneş\'in ateşiyle bütünleşen Leona, savaşçı Solari şövalyelerinden biri. Targon Dağı\'nı, Ufuk Kılıcı ve Şafak Kalkanı\'yla koruyor. Gözleri, içindeki semavi Suret\'in gücüyle ışıldarken;...'),
	(90, 'Malzahar', 'Malzahar', 'Hiçlik\'in Habercisi', 'Shurima güneşinin altında birçok insan delirdi; ama Malzahar akli dengesini, gecenin soğuk kollarında terk etti. Malzahar doğuştan kâhindi, kehanet yeteneğiyle kutsanmıştı. Yeteneği işlenmemiş olmakla beraber, Runeterra\'ya bahşedilen en büyük...'),
	(91, 'Talon', 'Talon', 'Bıçağın Gölgesi', '\'\'Valoran\'daki en ölümcül üç bıçak ustası da Du Couteau ailesine bağlıdır: Babam, ben ve Talon. Cesaretiniz varsa bize meydan okuyun.\'\'<br>-- Katarina Du Couteau<br><br>Talon\'un ilk anıları, Noxus\'un yeraltı geçitlerinin karanlığı ve bir bıçağın güven...'),
	(92, 'Riven', 'Riven', 'Sürgün', '\'\'İçimizdeki şeytanlara sinsice yataklık eden bir yer var; savaşın ve cinayetin kesiştiği sınırda.\'\'<br><br>Noxus\'ta cinsiyet, ırk, toplumsal sınıf farkı olmaksızın her yurttaş iktidar sahibi olabilir. Önemli olan tek şey güçtür. Riven, işte bu ideale...'),
	(96, 'Kog\'Maw', 'KogMaw', 'Cehennemin Dişleri', '\'\'Bu, sadece aç haliyse öfkeli halini görmek bile istemem.\'\'<br><br>Kâhin Malzahar Icathia\'da yeniden doğduğunda, zihnine demir atan uğursuz bir ses tarafından oraya yönlendirilmişti. İçinden gelen bu ses, ona korkunç bir amaç bahşetti ve artık...'),
	(98, 'Shen', 'Shen', 'Alacakaranlığın Gözü', '\'\'Göz ne korkuyu görür, ne nefreti ne de sevgiyi. Dengeyi bozacak her şeye karşı kördür.\'\'<br><br>Gizemli savaşçılardan oluşan gizli bir klanın önderi olan Shen, dünyaya dengeyi getirmekle yükümlü Alacakaranlığın Gözü unvanıyla hizmet eder. Duyguların...'),
	(99, 'Lux', 'Lux', 'Işık Leydisi', 'Demacia\'nın hizmetindeki erdem timsali, saygın Crownguards ailesinden gelen Luxanna\'nın kaderinde yükselmek vardı. Ailesinin tek kızı olarak büyüdü. Crownguards gibi üst düzey bir aileden beklenen şatafatlı davetlere ve yoğun eğitime hemen ayak uydurdu...'),
	(101, 'Xerath', 'Xerath', 'Yüksek Büyücü', '\'\'Ömür boyu esaret, beni hükümdarlığa hazırladı.\'\'<br><br>Antik Shurima\'nın Yükselmiş Büyücüsü Xerath, parçalanmış bir lahit içinde titreşen büyü gücünden oluşur. Binlerce yıl boyunca, çöl kumullarının altında esir kalmıştı; ancak Shurima\'nın...'),
	(102, 'Shyvana', 'Shyvana', 'Ejder Kanı', 'Ejderhayla insanın birlikteliğinden doğma bir melez olan Shyvana, hayatı boyunca aidiyet hissinin peşinden koştu. Gördüğü kötü muamele ve önyargı, onu vahşi bir savaşçıya dönüştürdü; Shyvana\'nın yoluna çıkmaya cüret edenler, içindeki ateşli canavarla...'),
	(103, 'Ahri', 'Ahri', 'Dokuz Kuyruklu Tilki', 'Güney Ionia Ormanları\'nda dolaşan diğer tilkilerin aksine, Ahri etrafındaki büyülü dünyaya karşı tuhaf bir bağlantı hissediyordu; her nedense bir şeylerin eksik kaldığı bir bağlantı... İçten içe, dünyaya geldiği halinin kendisine uymadığını düşünüyor...'),
	(104, 'Graves', 'Graves', 'Kanun Kaçağı', 'Malcolm Graves ziyaret ettiği her ülkenin, her imparatorluğun, her şehrin güvenlik güçleri tarafından aranan bir adamdır. Kuvvetli, iradeli ve hepsinden öte bir hayli insafsız olan Graves, suçla dolu yaşamı boyunca ufak bir servet elde etmiştir. Ancak...'),
	(105, 'Fizz', 'Fizz', 'Dalgaların Dümencisi', 'Yüzyıllar önce, sualtında yaşayan eski mi eski bir ırk, denizdeki bir dağın altına gizli bir şehir inşa etti. Düşmanları vardı var olmasına; ama şehir geçilmez bir kaleydi. Sualtı ırkı, bu kalenin güvenli duvarları içinde rahata alıştı. Fizz\'de ise...'),
	(106, 'Volibear', 'Volibear', 'Kükreyen Fırtına', 'Freljord\'un acımasız kuzey uçları; çorak tundraya binlerce yıldır göğüs gererek hayatta kalabilmiş savaşçı bir ırk olan Ursinlere ev sahipliği yapar. Ursinlerin lideri; yıldırımın gücünü kontrol ederek düşmanlarının yüreğine korku salan dişli savaşçı...'),
	(107, 'Rengar', 'Rengar', 'Gölge Avcı', 'Ganimet avcısı Rengar, ininin her bir duvarını Valoran\'ın en yırtıcı yaratıklarının başlarıyla, boynuzlarıyla, pençeleriyle ve dişleriyle süslemiştir. Koleksiyonu geniş; ama o hâlâ tatmin olmuş değil. Yorulmak bilmeden daha büyük avlar arıyor. Her avına...'),
	(110, 'Varus', 'Varus', 'İntikam Oku', '\'\'Ok kısa ömürlüdür ama ne yönünden sapar, ne de amacından.\'\'<br><br>Varus, yay kullanmadaki rakipsiz becerisinden ve sorgulanamaz onurundan dolayı kutsal bir Ionia tapınağının koruyucusu olarak seçilmişti. Tapınak, Ionia Yaşlılarının adayı karanlığa...'),
	(111, 'Nautilus', 'Nautilus', 'Derinliklerin Devi', 'Nautilus bir zamanlar, Savaş Enstitüsü tarafından Muhafız Denizi\'nin bilinmeyen noktalarını araştırmak için tutulmuş bir denizciydi. Bu keşif gezisi onu ve ekibini bilinmeyen suların açıklarına, okyanusun canlı mavi renginin yerini siyah ve hiçbirinin...'),
	(112, 'Viktor', 'Viktor', 'Makine Elçi', 'Viktor bilime ve icatlara, özellikle de mekanik otomasyon alanındaki icatlara duyduğu tutkuyu erken yaşlarda fark etti. Zaun\'un seçkin Tekmaturji Koleji\'ne gitti ve Blitzcrank\'ı yapan ekibi yönetti. Bu bilimsel gelişmenin, onu kariyerinin en tepesine...'),
	(113, 'Sejuani', 'Sejuani', 'Kuzeyin Hiddeti', 'Sejuani zorluklar içinde doğup, barbarlıkla büyüdü. Başkaları Freljord\'un zorlukları altında ezilip yok olurken, Sejuani açlık bir teşviğe, acı güce, soğuk ise zayıfları sürüden ayıklayan bir yardımcıya dönüşene kadar onlara göğüs gerdi. Yaşadığı...'),
	(114, 'Fiora', 'Fiora', 'Büyük Düellocu', '\'\'Seni öldürmek bir onur meselesi. Sende onurun zerresi bile yok ama yine de öleceksin.\'\'<br>Valoran\'ın en korkulan silahşoru olan Fiora; mavi çelikten kılıcının hızı kadar, acımasızlığı ve keskin zekâsıyla da tanınır. Demacia Krallığı\'ndan Laurent...'),
	(115, 'Ziggs', 'Ziggs', 'Sihirmayın Uzmanı', 'Ziggs, makine kurcalamaya doğuştan yetenekliydi. Ama düzensiz, hiperaktif yaradılışına yordle bilimadamları arasında sık rastlanmazdı. Heimerdinger gibi saygıdeğer bir mucit olmayı amaçlayarak, gözü yükseklerde projelerin arasında koşturuyordu. Eline...'),
	(117, 'Lulu', 'Lulu', 'Peri Sihirbaz', 'Lulu, herhalde Lig şampiyonlarının en başına buyruk olanı. Bandle Şehri\'nde geçirdiği gençliğinde, çoğunlukla ormanda tek başına dolaşır veya hayal kurardı. Asosyalliğinden değil; sadece Bandle Şehri\'ndeki gündelik koşuşturmalar onun hayal dünyası ile...'),
	(119, 'Draven', 'Draven', 'Görkemli Cellat', 'Kardeşi Darius\'un aksine, savaşta zafer kazanmak Draven\'a hiç yetmedi. Tanınmak, övülmek ve şan istiyordu. Bunları önce Noxus ordusunda aradı; ama gösterişe olan merakı hiç hoşgörülmedi. \'\'Draven\'\'ı dünyaya duyurmanın bir yolunu ararken, dikkati...'),
	(120, 'Hecarim', 'Hecarim', 'Savaşın Gölgesi', '\'\'Saflarını dağıt ve hiç acımadan onları nallarının altında ez. Canlıları parçala ve dehşetleriyle mest ol.\'\'<br><br>Hecarim, ölümcül hortlak süvari ordusunun başında Gölge Adalar\'dan dehşet seferine çıkan, heybetli ve zırhlı bir devdir. İnsanla...'),
	(121, 'Kha\'Zix', 'Khazix', 'Hiçlik Yağmacısı', 'Kha\'Zix, Hiçlik\'ten gelen acımasız bir avcı. Valoran\'a, bu dünyanın en çok güç vaat eden canlılarını silip süpürmeye geldi. Öldürdüğü her avın gücünü bünyesine katarak, daha da gelişip güçleniyor. Kha\'Zix en çok, dengi kabul ettiği tek yaratık olan...'),
	(122, 'Darius', 'Darius', 'Noxus\'un Eli', 'Noxus ordusunun, savaşta bilenmiş en korkulu savaşçısı olan Darius\'tan daha yüce bir sembolü yoktur. Genç yaşta yetim kalan Darius, kendisini ve küçük kardeşini yaşatmak için mücadele etmek zorunda kaldı. Orduya katıldığında kıdemli bir askerin gücünü...'),
	(126, 'Jayce', 'Jayce', 'Yarınların Koruyucusu', 'Zekâsı, karizması ve icat ettiği, şekil değiştirebilen çekiciyle Jayce, memleketi Piltover\'ı korumak için yaşıyor. Daha halkı tarafından kahraman olarak selamlanmadan çok önce, umut vaat eden, genç bir mucitti. Piltover yöneticileri onu nadide ve...'),
	(127, 'Lissandra', 'Lissandra', 'Buz Cadısı', 'Lissandra\'nın büyüleri, buzun saf gücünü karanlık ve korkunç bir şeye dönüştürüyor. Bu cadı, sahip olduğu karanlık buzun gücüyle rakiplerini dondurmaktan çok daha fazlasını yaparak, kendisine karşı gelenleri delik deşik ve paramparça ediyor. Kuzeyin...'),
	(131, 'Diana', 'Diana', 'Ay\'ın Hiddeti', '\'\'Ben Ay\'ın ruhunda akan ışığım.\'\'<br><br>Diana, elindeki hilâl şeklindeki Ay bıçağıyla, Targon Dağı ve civarında yasaklanmış bir inanç olan Lunari\'nin savaşçısı olarak mücadele ediyor. Karın, soğuk kış geceleri aldığı renkteki ışıl ışıl zırhıyla adeta...'),
	(133, 'Quinn', 'Quinn', 'Demacia\'nın Kanatları ', 'Quinn\'le Valor, iki üyeden oluşan seçkin bir kolcu ekibidir. Demacia için hızla yapılması gereken bilgi toplama görevlerinden ölümcül saldırılara kadar en tehlikeli görevleri üstlenirler. Aralarındaki kopmaz bağ, savaş alanında ölümcül hale gelir...'),
	(134, 'Syndra', 'Syndra', 'Gücün Karanlık Yüzü', 'Muazzam bir büyü yeteneğiyle doğan Syndra, hükmettiği inanılmaz gücü kullanmaya bayılıyor. Her geçen gün, sahip olduğu sihir gücü daha da artıyor, daha da yıkıcı hale geliyor. Denge veya kısıtlama gibi kavramları reddeden Syndra\'nın istediği tek şey...'),
	(136, 'Aurelion Sol', 'AurelionSol', 'Yıldızların Hâkimi', 'Aurelion Sol, bir zamanlar kozmosun engin boşluğunu kendi eseri olan gökyüzü harikalarıyla donatmasıyla tanınır. Şimdilerdeyse muhteşem gücü, kendisini kandırıp köleleştiren uzay kâşifi bir imparatorluğun boyunduruğunda. Yıldızları işlediği günlere...'),
	(143, 'Zyra', 'Zyra', 'Dikenli Cazibe', 'Ölmekte olan bitki Zyra, kaderinin dizginlerini eline alma arzusuyla, hayata baştan başlamak için bilincini bir insan vücuduna aktardı. O ve türdeşleri yüzyıllar önce Kumungu Ormanı\'nda sarmaşık dallarını ve dikenlerini kullanarak egemenlik kurmuş...'),
	(150, 'Gnar', 'Gnar', 'Kayıp Halka', 'Orman, görmeyen gözleri affetmez. Her kırık dal parçası bir hikâye anlatır.<br><br>Bu ormanın sunduğu her türden yaratığı avladım. Hünerlerimi sınayacak başka bir şey kalmadı sanırdım. Ama şimdi görüyorum ki, yeni bir şey var. Bıraktığı izlerden teki...'),
	(154, 'Zac', 'Zac', 'Gizli Silah', 'Zaun\'un hexkimya mühendisliğiyle tasarlanmış bir süper asker olan Zac\'in açılımı; Zaun\'lu Askerî Cisim\'dir. Kaba kuvvetin sınırsız esneklikle birleştiği Zac; engellerin üzerinden sekip, rakiplerini safdışı bırakıyor. Her ne kadar bir silah...'),
	(157, 'Yasuo', 'Yasuo', 'Günahkâr Kılıç', 'Yasuo azmiyle dikkat çeken, atik bir kılıç ustasıdır. Düşmanlarını devirmek için bizzat rüzgâr kuşanıp estirir. Bu mağrur savaşçı, düzmece bir itham sonucunda itibarını yitirmiş ve hayatta kalmak için umutsuz bir savaşın içine itilmiştir. Dünya âlemi...'),
	(161, 'Vel\'Koz', 'Velkoz', 'Hiçlik\'in Gözü', 'Aniden parlayan ışığın içinden geçiyorum. Kıps, kıps, kıps, kıps. Gözüm alıştığında, önümde uzanan araziyi ölçüp biçiyorum.<br><br>Bir hareketlenme var. Aşağı baktığımda arka ayakları üzerinde duran ve bedenimi koklayan küçük, beyaz bir yaratık...'),
	(163, 'Taliyah', 'Taliyah', 'Taşların Dokuyucusu', 'Taliyah, taşları coşkulu bir enerji ve katıksız bir azimle dokuyan, Shurima\'lı göçebe bir büyücü. Gençliğin merakı ve yetişkinliğin sorumlulukları arasında gelgitler yaşayan Taliyah, her gün daha da artan güçlerinin gerçek doğasını keşfedebilmek için...'),
	(164, 'Camille', 'Camille', 'Çelik Gölge', 'Kanunun sınırları dışında hareket edebilmek için tepeden tırnağa bir silaha dönüştürülen Camille Ferros, Piltover ve arka bahçesi Zaun\'daki ticari işlerin pürüzsüz bir şekilde yürüdüğünden emin olmakla görevli, zarif ve seçkin bir ajandır. Görgü ve para...'),
	(201, 'Braum', 'Braum', 'Freljord\'un Yüreği', '\'\'Sana bir masal anlatayım ister misin?\'\'<br><br>\'\'Ben masal dinleyecek kadar küçük değilim ki Nine.\'\'<br><br>\'\'Olur mu hiç? Güzel bir hikâyeye kim hayır diyebilir?\'\'<br><br>Kız biraz istemeyerek de olsa yatağına girip beklemeye başladı, bu savaşı...'),
	(202, 'Jhin', 'Jhin', 'Virtüöz', '\'\'Sanat kimsenin gözünün yaşına bakmaz.\'\'<br><br>Jhin cinayetin sanat olduğuna inanan, titiz mi titiz, cani bir psikopat. Bir zamanlar Ionia hapishanelerinde çürürken bir anda Ionia\'yı yöneten meclisteki karanlık odaklar tarafından serbest bırakılan bu...'),
	(203, 'Kindred', 'Kindred', 'Madalyonun İki Yüzü', '\'\'Bir daha söyle kuzucuk, şimdi bunlardan hangileri bizim?\'\'<br>\'\'Hepsi, sevgili Kurt.\'\'<br>Kendi içinde ayrışan ama hiç ayrı düşmeyen Kindred, ölümün ikiz özlerini temsil ediyor. Kuzu\'nun oku, kaderini kabul edenleri tez elden azat ediyor. Kurt ise...'),
	(222, 'Jinx', 'Jinx', 'Delifişek', 'Jinx, sağı solu belli olmayan, kafadan kontak bir suçlu; en nefret ettiği şeyse can sıkıntısı. Sonuçlarını bir an bile düşünmediği karışıklıklar çıkarmak için yaşıyor ve arkasında, korku ve panikten oluşan bir iz bırakıyor. Marifetlerinden en çok...'),
	(223, 'Tahm Kench', 'TahmKench', 'Nehrin Kralı', '\'\'Dünya koca bir nehir, ben de onun kralıyım.\'\'<br>Runeterra\'nın akarsularını kendine mesken belleyen Tahm Kench, doymak bilmez iştahını bastırmak için biçare ruhların acılarından beslenir. Tarifi imkansız bir cazibeye sahip olan bu gurme, kurbanlarının...'),
	(236, 'Lucian', 'Lucian', 'İnfazcı', 'Kadim mi kadim bir güçle donatılmış birer yadigâr olan silahlarıyla, Lucian; yaşayan ölülerin yarattığı tehlikenin karşısında duran, yılmaz bir bekçi. Sahip olduğu katı kararlılıktan, arındıran saldırılarıyla yok ettiği, akla hayale sığmaz dehşetler...'),
	(238, 'Zed', 'Zed', 'Gölgelerin Efendisi', 'Kadim çağlardan kalma yasak yöntemlerin sırrına son 200 yıldır ilk erişebilen ninja, Zed oldu. Kendisini ömrü boyunca bağlamış olan denge ve disiplin öğretilerinden koparak, klanına ve ustasına karşı geldi. Şimdi Zed gölgelerin bilgeliğini kucaklayan...'),
	(240, 'Kled', 'Kled', 'Deli Bozuk Süvari', '\'\'Aklı başında olan tabanları yağlar... Ama benim kitabımda kaçmak yok!\'\'<br><br>Korkusuz olduğu kadar aksi bir savaşçı olan Kled, Noxus\'un gözde halk kahramanlarından biridir. Ülkesinin vahşi cesaretinin ayaklı hâli olarak imparatorluk askerlerinin...'),
	(245, 'Ekko', 'Ekko', 'Zamanı Kıran Çocuk', 'Zaun\'un tekinsiz sokaklarından gelen yaman delikanlı Ekko, zamana istediği gibi müdahale ederek her türlü durumu lehine çeviriyor. Kendi icadı olan Zaman Kapsülü\'nü kullanarak gerçekliğin sonsuz ihtimallerinde keşfe çıkan Ekko, çok boyutlu olasılıklarla...'),
	(254, 'Vi', 'Vi', 'Piltover Fedaisi', 'Vi\'a göre her sorun, devasa hextech eldivenleriyle yıkıp geçeceği birer tuğla duvardan ibaret. Kanun dışı bir ortamda büyüyen Vi, suç dünyasına dair bilgisini artık Piltover\'ın asayiş kuvvetlerinde hizmet etmekte kullanıyor. Vi\'ın atılgan tavırları...'),
	(266, 'Aatrox', 'Aatrox', 'Darkin Kılıcı', 'Aatrox, Darkin adıyla bilinen bir ırkın son beş üyesinden biri olan efsanevi bir savaşçı. Devasa kılıcını büyük bir zarafet ve ustalıkla taşıyor, üzerine gelen orduları biçerken, büyüleyici bir görünüm arz ediyor. Aatrox\'un canlı gibi duran kılıcı...'),
	(267, 'Nami', 'Nami', 'Dalga Çağıran', 'Nami, okyanusun ham enerjisini yönlendirip şifa veren yanını kullanıyor ve dalgaların saf gücüne hükmediyor. Pek çok kişi ondan şüphe etse de, Nami kimsenin cesaret edemediği tehlikeli bir göreve atılacak kadar cesur ve azimli. Halkı şimdi onun, ırkının...'),
	(268, 'Azir', 'Azir', 'Kumların İmparatoru', '\'\'Shurima, bir zamanlar Runeterra\'nın medar-ı iftiharıydı. Onu tekrar eski günlerine döndüreceğim.\'\'<br><br>Ölümsüzlüğün eşiğine yaklaşmış gururlu bir adam olan Azir, çok eski zamanlarda Shurima\'nın ölümlü bir imparatoruydu. Zaferlerin en büyüğünü...'),
	(412, 'Thresh', 'Thresh', 'Zincirli Gardiyan', '\'\'Bir zihnin parçalarını tek tek ayırmak kadar keyifli bir şey yok doğrusu.\'\'<br><br>Thresh kurnaz buluşları sayesinde, acılar içinde kıvrandırdığı fanilerin iradelerini yavaş yavaş kırmasıyla nam salmış ve bununla gurur duyan, sadist bir hortlaktır...'),
	(420, 'Illaoi', 'Illaoi', 'Ruhani Rehber', '\'\'Vaaza gerek yok. Kırık kemiklerin verdiği ders unutulmaz.\'\'<br>Illaoi\'nin cüssesini gölgede bırakabilecek bir şey varsa, o da sarsılmaz inancıdır. Büyük Kraken\'in elçisi olarak kullandığı devasa, altın idolle rakiplerinin ruhlarını bedenlerinden...'),
	(421, 'Rek\'Sai', 'RekSai', 'Yeraltındaki Dehşet', 'Türünün en büyük ve en vahşi üyesi olan Rek\'Sai, kurbanlarını gafil avlamak için toprakta tüneller kazıp sonra da tek lokmada yutan acımasız bir yırtıcıdır. Doymaz açlığı, bir zamanlar pek ihtişamlı olan Shurima İmparatorluğu\'nun koskoca bölgelerini...'),
	(427, 'Ivern', 'Ivern', 'Doğasever Baba', 'Birçoklarının Doğasever Baba adıyla tanıdığı Ivern Dikenayak bir yandan Runeterra ormanlarını arşınlarken bir yandan da adım attığı her yerde yaşam filizlendiren, tuhaf mı tuhaf bir yarı ağaç, yarı insan. Tabiatın bütün sırlarına vakıf olan Doğasever...'),
	(429, 'Kalista', 'Kalista', 'İntikam Mızrağı', '\'\'Bize yanlış yaparlarsa, adaleti sağlarız. Bizi incitirlerse, karşılığını veririz. Bize ihanet ederlerse, İntikam Mızrağı onları paramparça eder!\'\'<br><br>Öç hırsı ve öfkeyle dolu bir varlık olan Kalista, çağırıldığında hain ve hilekârları avlamak için...'),
	(432, 'Bard', 'Bard', 'Kozmik Koruyucu', 'Bard, fanilerin aklının ve hayalinin alamayacağı alemlerde gezinip durur. Valoran\'lı nice büyük alim, bu gizemli seyyahın hikmetini anlamaya ömürlerini adadı. Valoran tarihi boyunca bu esrarengiz ruha pek çok isim layık görüldü. Kozmik Berduş ve Gizemli...'),
	(497, 'Rakan', 'Rakan', 'Gönülçelen', 'Çekiciliği kadar dengesizliğiyle de tanınan Rakan, ünlü bir vastaya baş belası ve Lhotlan kabilesinin tarihinde gördüğü en büyük savaş dansçısı. Ionia\'nın dağlarında yaşayan insanlar içinse Rakan ismi, çılgın festivaller, vahşi partiler ve düzensiz...'),
	(498, 'Xayah', 'Xayah', 'Asi', 'Xayah, halkını kurtarmak için bireysel savaş veren ölümcül ve kusursuz bir vastaya devrimcisi. Hızını, kurnazlığını ve jilet gibi keskin tüy bıçaklarını kullanarak yoluna çıkan herkesi yok ediyor. Xayah, ortağı ve sevgilisi Rakan ile omuz omuza...');
